import React from 'react'
import { Card, CardBody, Heading, Text, LinkExternal } from '@pancakeswap-libs/uikit'
import BigNumber from 'bignumber.js/bignumber'
import styled from 'styled-components'
import { getBalanceNumber } from 'utils/formatBalance'
import { useTotalSupply, useBurnedBalance } from 'hooks/useTokenBalance'
import useI18n from 'hooks/useI18n'
import { getCakeAddress } from 'utils/addressHelpers'
import CardValue from './CardValue'
import { useFarms, usePriceCakeBusd } from '../../../state/hooks'


const StyledLinkExternal = styled(LinkExternal)`
  text-decoration: none;
  font-weight: normal;
  color: ${({ theme }) => theme.colors.text};
  display: flex;
  align-items: center;

  svg {
    padding-left: 4px;
    height: 18px;
    width: auto;
    fill: ${({ theme }) => theme.colors.primary};
  }
`

const StyledCakeStats = styled(Card)`
  margin-left: auto;
  margin-right: auto;
`

const Row = styled.div`
  align-items: center;
  display: flex;
  font-size: 14px;
  justify-content: space-between;
  margin-bottom: 8px;
`

const CakeStats = () => {
  const TranslateString = useI18n()
  const totalSupply = useTotalSupply()
  const burnedBalance = useBurnedBalance(getCakeAddress())
  const farms = useFarms();
  const eggPrice = usePriceCakeBusd();
  const circSupply = totalSupply ? totalSupply.minus(burnedBalance) : new BigNumber(0);
  const cakeSupply = getBalanceNumber(circSupply);
  const marketCap = eggPrice.times(circSupply);

  let eggPerBlock = 0;
  if(farms && farms[0] && farms[0].eggPerBlock){
    eggPerBlock = new BigNumber(farms[0].eggPerBlock).div(new BigNumber(10).pow(18)).toNumber();
  }

  return (
    <StyledCakeStats>
      <CardBody>
        <Heading size="xl" mb="24px">
          {TranslateString(534, 'GOOSE Stats')}
        </Heading>
        <Row>          <Text fontSize="14px">{TranslateString(10005, 'Market Cap')}</Text>
          <CardValue fontSize="14px" value={getBalanceNumber(marketCap)} decimals={0} prefix="$" />
        </Row>
        <Row>
          <Text fontSize="14px">{TranslateString(532346, 'Total Supply')}</Text>
          {totalSupply && <CardValue fontSize="14px" value={1000000} decimals={0} />}
        </Row>
        <Row>
          <Text fontSize="14px">{TranslateString(523438, 'Total Burned')}</Text>
          {cakeSupply && <CardValue fontSize="14px" value={1000000 - cakeSupply} decimals={0} />}
        </Row>
        <Row>
          <Text fontSize="14px">{TranslateString(10234004, 'Circulating Supply')}</Text>
          {cakeSupply && <CardValue fontSize="14px" value={cakeSupply} decimals={0} />}
        </Row>     <Row>
          <Text fontSize="14px">{TranslateString(100205, 'Deflationary')}</Text>
          10 %
        </Row>
                     <Row>
        <StyledLinkExternal href='https://bscscan.com/token/0xC2Ef78B228F2170fC5C0ca3d9F4BFF7d3069Aa8f'> GOOSE Contract Address </StyledLinkExternal>
        </Row>
                             <Row>
        <StyledLinkExternal href='https://bscscan.com/address/0x2e02CFCa27b57408DE523156c334B1f8BA37bA7C'> Master Chef Contract Address </StyledLinkExternal>
        </Row>
      </CardBody>
    </StyledCakeStats>
  )
}

export default CakeStats
